import axios, { Axios } from "axios";
import { useEffect, useState } from "react";


const Post = ()=>{
  const [list_of_post,setList_Of_Posts] = useState([]);

useEffect(()=>{
  async function handlePosts(){
    try{
    const posturl = `http://localhost:8000/api/posts/`;
    const postdata = await axios.get(posturl).then(response =>{
           setList_Of_Posts(response.data);
    })}
    catch(e){
      console.log(e);
    }
  }
  try{
      handlePosts();}
  catch(e){
      console.log(e);
      }

})
  
  return(
    <div>
      {
        list_of_post.map((post)=>{
          return(
            <div>
              <div >
                <h1>{post.username}</h1>
                <br></br>
              </div>
              <div>
                <p>{post.feed}</p>
                <br></br>
              </div>
              <div>
                <h1>{post.comment}</h1>
              </div>
            </div>
          )
        })
        
      }
    </div>
  )
}

export default Post;
