import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import './Login.style.css'

const Login = ({props,route,navigation})=>{
    const [useremail,setUseremail] = useState();
    const [userpass,setUserpass] = useState();
    const [userdata,setUserdata] = useState();
    
    // using useeffect to maintain the loginstate of the user;
    useEffect(()=>{
        if(localStorage.getItem("user")){
            // navigate('/admin',{state:{userstatus:userstatus}})
        }
    })
    const navigate = useNavigate();

    // Loggin Function
    async function handleLogin(){
        const posturl = `http://localhost:8000/login/${useremail}`;
        const data =  axios.post(posturl).then(response =>{
            return(
                setUserdata(response.data)
            )
        });
        if(data.status === 202){
            alert("User is successfully logged in");
            localStorage.setItem("user",useremail);
            // setStatus(true);
            // navigate('/admin',{state:{userstatus:userstatus}})
        }
        
        else{
            alert("Incorrect Credentials")
            // navigate('/admin',{state:{userstatus:userstatus}})
        }
        


    }
    return (
        <div className="logincontainer">
            <div className="logininput">
                <h1>Login</h1>
                <input className="uservalues" type="email" placeholder="Email" value={useremail} onChange={(e)=>{
                    setUseremail(e.target.value);
                }}/>
                <input className="uservalues" type="password" placeholder="Password" value={userpass} onChange={(e)=>{
                    setUserpass(e.target.value);
                }}/>
            <div className="login">
                <button id = "loginbtn" onClick={handleLogin}>Login</button>
                <button id = "loginbtn" onClick={()=>{
                    navigate('/')
                }}>SignIn</button>
            </div>
            </div>
            

        </div>
    )
}
export default Login;
