import axios from "axios";
import { useState } from "react";
import { Navigate, useNavigate } from "react-router-dom";
import NavBar from "../NavBar/NavBar.component";
import './Regestration.style.css'

const Regestration = ()=>{
    const [name,setName] = useState();
    const [email,setEmail] = useState();
    const [pass,setPass] = useState();
    const [id,setId] = useState();
    // const navigate = useNavigate();
    // submit function
    async function handleSubmit(){
        setId(randomNumberInRange(1,10000));
        console/log(id+name+email+pass)
        const posturl = `http://localhost:8000/users`;
        const postdata = await axios.post(posturl,{id:id,username:name,email:email,password:pass}).then(response =>{
            return response.status;
        })
        if(postdata.status == 201){
            alert("You are successefully registered");
            // navigate('/login')
        }
    
     }
     function randomNumberInRange(min, max) {
        return Math.round(Math.random() * (max - min + 1)) + min;
      }

    return(
        <div>
            <div className="container">
                <div className="innercontainer">
                    <h1>SignUp</h1>
                    <input  className="inputcontainer" type="text" value={name} placeholder="Username" onChange={(e)=>{
                            setName(e.target.value);
                    }}/>
                    <input  className="inputcontainer" type="Email" value = {email} placeholder="Email" onChange={(e)=>{
                            setEmail(e.target.value);
                    }}/>
                    <input type="password" className="inputcontainer"  value={pass} placeholder="Password" onChange={(e)=>{
                            setPass(e.target.value);
                    }}/>
                    <div className="submitbutton">
                    <button id="submit" onClick={handleSubmit}>Submit</button>
                </div>
                </div>
                
            </div>
        </div>
    )
}
export default Regestration
