import "./NavBar.style.css";
import React, { useState } from "react";
import HomeIcon from "@material-ui/icons/Home";
import FeaturedPlayListOutlinedIcon from "@material-ui/icons/FeaturedPlayListOutlined";
import AssignmentTurnedInOutlinedIcon from "@material-ui/icons/AssignmentTurnedInOutlined";
import PeopleAltOutlinedIcon from "@material-ui/icons/PeopleAltOutlined";
import NotificationsOutlinedIcon from "@material-ui/icons/NotificationsOutlined";
import SearchIcon from "@material-ui/icons/Search";
import LanguageIcon from "@material-ui/icons/Language";
import Modal from "react-modal"
import TextField from '@mui/material/TextField';
import { Avatar, Button, Input } from "@material-ui/core";
import { useDispatch, useSelector } from "react-redux";
// import { selectUser } from "../features/userSlice";
import { ExpandMore, Link } from "@material-ui/icons";
import { regestrationActions } from "../../store/RegestrationSlice";
import { postActions } from "../../store/PostFeedSlice";
import axios from "axios";
import { Navigate, useNavigate } from "react-router-dom";



Modal.setAppElement("#root");

const NavBar =()=> {
//   const user = useSelector(selectUser);

  const [IsmodalOpen, setIsModalOpen] = useState(false);
  const [input, setInput] = useState("");
  const [inputUrl, setInputUrl] = useState("");
  const questionName = input;
  const userdata = JSON.parse(localStorage.getItem("users"))
  const postdetails = useSelector((state)=>state.post.postdata)
  const dispatch = useDispatch()
  const [status,setStatus] = useState();
  console.log(postdetails);
  const randomNumberInRange = (min, max) => {
    return Math.round(Math.random() * (max - min + 1)) + min;
  }
const navigate = useNavigate();
  const handleLogout =()=>{
    if(userdata){
        localStorage.clear();
        navigate('/')
        window.location.reload(true);
  }
  else{
    navigate('/')
  }

}



  const handlePostSubmit = async () => {
    dispatch(postActions.setPostUserId(userdata.id))
    dispatch(postActions.setPostId(randomNumberInRange(1,1000)))
    dispatch(postActions.setPostUsername(userdata.username))
    try{
        const url = "http://localhost:8000/api/posts/";
        const post = await axios.post(url,postdetails).then((response)=>{
                  setStatus(response.status);
        })
        alert("Successful")
        setIsModalOpen(false);
    }
    catch(e){
      console.log(e);
    }
    
  };

  return (
    <div className="qHeader">
      <div className="qHeader__logo">
        <h1>Express</h1>
      </div>
      <div className="qHeader__icons">
        <div className="active qHeader__icon">
          <HomeIcon onClick={()=>{
            navigate("/")
          }} />
        </div>
        <div className="qHeader__icon">
          <FeaturedPlayListOutlinedIcon />
        </div>
        <div className="qHeader__icon">
          <AssignmentTurnedInOutlinedIcon />
        </div>
        <div className="qHeader__icon">
          <PeopleAltOutlinedIcon />
        </div>
        <div className="qHeader__icon">
          <Button onClick={()=>{
            navigate('/register')
            window.location.reload(true);
          }}>SignUp</Button>
        </div>
      </div>
      <div className="qHeader__input">
        <SearchIcon />
        <input type="text" placeholder="Search Express" />
      </div>
      <div className="qHeader__Rem">
        <div className="qHeader__avatar">
          <Avatar
            // onClick={}
            className="Avatar"
            src={
            //   user.photo
                // ? user.photo
                 "https://images-platform.99static.com//_QXV_u2KU7-ihGjWZVHQb5d-yVM=/238x1326:821x1909/fit-in/500x500/99designs-contests-attachments/119/119362/attachment_119362573"
            }
          />
        </div>
        
        <Button onClick={handleLogout}>LogIn/LogOut</Button>
       
        <Button onClick={() => setIsModalOpen(true)}>Add Post</Button>
        <Modal
          isOpen={IsmodalOpen}
          onRequestClose={() => setIsModalOpen(false)}
          shouldCloseOnOverlayClick={false}
          style={{
            overlay: {
              width: 700,
              height: 600,
              backgroundColor: "rgba(0,0,0,0.8)",
              zIndex: "1000",
              top: "50%",
              left: "50%",
              marginTop: "-300px",
              marginLeft: "-350px",
            },
          }}
        >
          <div className="modal__title">
            <h5>Add Link</h5>
            <h5>Share Link</h5>
          </div>
          <div className="modal__info">
            <Avatar
              className="avatar"
              src={
                   "https://images-platform.99static.com//_QXV_u2KU7-ihGjWZVHQb5d-yVM=/238x1326:821x1909/fit-in/500x500/99designs-contests-attachments/119/119362/attachment_119362573"
              }
            />
            {/* <p>{user.disPlayName ? user.disPlayName : user.email} asked</p> */}
            <div className="modal__scope">
              <PeopleAltOutlinedIcon />
              <p>Public</p>
              <ExpandMore />
            </div>
          </div>
          <div className="modal__Field">
          <TextField
        //   id="filled-multiline-flexible"
          id="filled-textarea"
          label="Start Writing Here"
          multiline
          value={postdetails.feed}
          onChange={(e) => dispatch(postActions.setFeed(e.target.value))}
          variant="filled"
        />
            <div className="modal__fieldLink">
              <Link />
              <input
                value={postdetails.feed}
                onChange={(e) => dispatch(postActions.setFeed(e.target.value))}
                type="text"
                placeholder="Optional: inclue a link that gives context"
              ></input>
            </div>
          </div>
          <div className="modal__buttons">
            <button className="cancle" 
             onClick={() => setIsModalOpen(false)}
            >Cancel</button>
            <button type="sumbit" onClick={handlePostSubmit} className="add">
              Post
            </button>
          </div>
        </Modal>
      </div>
    </div>
  );
}

export default NavBar;
