import { Avatar } from "@material-ui/core";
import React, { useEffect, useState } from "react";
import "./Post.style.css";
import ArrowUpwardOutlinedIcon from "@material-ui/icons/ArrowUpwardOutlined";
import ArrowDownwardOutlinedIcon from "@material-ui/icons/ArrowDownwardOutlined";
import RepeatOutlinedIcon from "@material-ui/icons/RepeatOutlined";
import ChatBubbleOutlineOutlinedIcon from "@material-ui/icons/ChatBubbleOutlineOutlined";
import { MoreHorizOutlined, ShareOutlined } from "@material-ui/icons";
import { useDispatch, useSelector } from "react-redux";
import Modal from "react-modal";
import axios, { Axios } from "axios";
import { commentActions } from "../../store/CommentSlice";

const Post = (props) => {
    const commentdetails = useSelector((state)=> state.commentdata);
    const dispatch = useDispatch();
    const randomNumberInRange=(min, max)=> {
      return Math.round(Math.random() * (max - min + 1)) + min;
    }

  
  const [IsmodalOpen, setIsModalOpen] = useState(false);
  //   const questionId = useSelector(selectQuestionId);
  const [comment, setComment] = useState("");
  const [getAnswers, setGetAnswers] = useState([]);
  const [list_of_post, setList_Of_Posts] = useState([]);



  useEffect(() => {
    async function handlePosts() {
      try {
        const posturl = `http://localhost:8000/api/posts/`;
        const postdata = await axios.get(posturl).then(response => {
          setList_Of_Posts(response.data);
        })
      }
      catch (e) {
        console.log(e);
      }
    }
    try {
      handlePosts();
    }
    catch (e) {
      console.log(e);
    }

  },[])



  //   useEffect(() => {
  //     if (questionId) {
  //       db.collection("questions")
  //         .doc(questionId)
  //         .collection("answer")
  //         .orderBy("timestamp", "desc")
  //         .onSnapshot((snapshot) =>
  //           setGetAnswers(
  //             snapshot.docs.map((doc) => ({ id: doc.id, answers: doc.data() }))
  //           )
  //         );
  //     }
  //   }, [questionId]);


  //   for c, i in session.query(Customer, Invoice).filter(Customer.id == Invoice.custid).all():
  //  print ("ID: {} Name: {} Invoice No: {} Amount: {}".format(c.id,c.name, i.invno, i.amount))
  async function handleComment(e) {
    try {
      await dispatch(commentActions.setCommentId(randomNumberInRange(1,100)))
      const  commenturl = `http://localhost:8000/api/comments/`;
      const commentdata = await axios.post(commenturl,commentdetails).then(response => {
        setComment(response.data);
      })
      console.log(commentdetails);
    }
    catch (e) {
      console.log(e);
    }
  }

  return (
    <div className="post">
      {
        list_of_post.map((post,props)=>{
          const val1 = post.id;
          return(
            <div className="list_container" key = {post.id}>
      <div className="post__info">
        <Avatar
          src={
          "https://images-platform.99static.com//_QXV_u2KU7-ihGjWZVHQb5d-yVM=/238x1326:821x1909/fit-in/500x500/99designs-contests-attachments/119/119362/attachment_119362573"
          }
        />
        <h4>{post.username ? post.username : post.email}</h4>
        <small>{new Date().getDate()}</small>
      </div>
      <div className="post__body">
        <div className="post__question">
          <p>{post.feed}</p>
          <button
            onClick={() => {
              dispatch(commentActions.setCommentUsername(post.username))
              setIsModalOpen(true)}}
            className="post__btnAnswer"
          >
            Comment
          </button>
          <Modal
            // keyval={val1}
            isOpen={IsmodalOpen}
            onRequestClose={() => setIsModalOpen(false)}
            shouldCloseOnOverlayClick={false}
            style={{
              overlay: {
                width: 680,
                height: 550,
                backgroundColor: "rgba(0,0,0,0.8)",
                zIndex: "1000",
                top: "50%",
                left: "50%",
                marginTop: "-250px",
                marginLeft: "-350px",
              },
            }}
          >
            <div className="modal__question">
              <h1>{post.id}</h1>
              <p>
                asked by{" "}
                <span className="name">
                   {props.dynData ? post.username : post.email} 
                </span>{" "}
                {""}
                on{" "}
                <span className="name">
                  {new Date().getDate()} 
                </span>
              </p>
            </div>
            <div className="modal__answer">
              <textarea
                value={comment}
                onChange={(e) =>{ 
                  setComment(e.target.value)
                  dispatch(commentActions.setCommentPostId(props.dynData))
                  dispatch(commentActions.setUserComment(comment)
                )}}
                placeholder="Enter Your Answer"
                type="text"
              />
              {console.log(commentdetails)}
            </div>
            <div className="modal__button">
              <button className="cancle" onClick={() => setIsModalOpen(false)}>
                Cancel
              </button>
              <button type="sumbit" onClick={handleComment} className="add">
                Add Answer
              </button>
            </div>
          </Modal>
        </div>
        {/* <div className="post__answer">
          {getAnswers.map(({ id, answers }) => (
            <p key={id} style={{ position: "relative", paddingBottom: "5px" }}>
              {/* {Id === answers.questionId ? ( */}
                {/* <span> */}
                  {/* {answers.answer} */}
                  {/* <br /> */}
              {/* /    <span */}
                    {/* style={{ */}
                      {/* position: "absolute", */}
                      {/* color: "gray", */}
                      {/* fontSize: "small", */}
                      {/* display: "flex", */}
                      {/* right: "0px", */}
                    {/* }} */}
                  {/* > */}
                    {/* <span style={{ color: "#b92b27" }}> */}
                      {/* {answers.user.displayName
                        ? answers.user.displayName
                        : answers.user.email}{" "}
                      on{" "} */}
                      {/* {new Date(answers.timestamp?.toDate()).toLocaleString()} */}
                    {/* </span> */}
                  {/* </span> */}
                {/* </span> */}
              {/* ) : ( */}
                {/* "" */}
              {/* )} */}
            {/* </p> */}
          {/* ))} */}
        {/* </div>  */}
        {/* <img src={imageUrl} alt="" /> */}
      </div>
      <div className="post__footer">
        <div className="post__footerAction">
          <ArrowUpwardOutlinedIcon />
          <ArrowDownwardOutlinedIcon />
        </div>

        <RepeatOutlinedIcon />
        <ChatBubbleOutlineOutlinedIcon />
        <div className="post__footerLeft">
          <ShareOutlined />
          <MoreHorizOutlined />
        </div>
      </div>
      </div>
      )})}
    </div>
  );
}

export default Post;
