import { useEffect, useState } from "react";

const Post = ()=>{
  const [list_of_post,setList_Of_Posts] = useState();

useEffect(()=>{
  async function handlePosts(){
    const posturl = `http://localhost:8000/api/posts/`;
    const postdata = await axios.post(posturl,userdetails).then(response =>{
           setList_Of_Posts(response.data);
    })
  }
  handlePosts();

})
  
  return(
    <div>
      {
        list_of_post.map((post)=>{
          return(
            <div>
              <div >
                <h1>{post.username}</h1>
                <br></br>
              </div>
              <div>
                <p>{post.feed}</p>
                <br></br>
              </div>
              <div>
                <h1>{post.comment}</h1>
              </div>
            </div>
          )
        })
        
      }
    </div>
  )
}

export default Post;
