import axios from "axios";
import { useEffect, useState } from "react";

const LoadComments = (props) => {

    const [listComment, setListComment] = useState([]);


    useEffect(() => {

        async function getComments() {
            try {
                const posturl = `http://localhost:8000/api/cmts/${13}`;
                const postdata = await axios.get(posturl).then(response => {
                    setListComment(response.data);
                })
            }
            catch (e) {
                console.log(e);
            }
        }
        try {
            getComments();
        }
        catch (e) {
            console.log(e);
        }
    }, [])

    return (
        <div>
                {
                    listComment.map((list) => {
                        return (
                            <div key={list.id}>
                                <p key={props.postid} style={{ position: "relative", paddingBottom: "5px" }}></p>
                                    
                                            <div>
                                            <span style={{
                                                position: "absolute",
                                                color: "gray",
                                                fontSize: "small",
                                                display: "flex",
                                                right: "0px",
                                            }}>
                                                <h3>{list.comment}</h3>
                                                <br />
                                                /    <span
                                                >
                                                    <span style={{ color: "#b92b27" }}>
                                                        {list.username
                                                            ? list.username
                                                            : "unidentified"}

                                                    </span>
                                                </span>
                                            </span>
                                            </div>
                                        
                                
                            </div>
                        )
                    })}
        </div>
    )
}
export default LoadComments;
