import { PostAddSharp } from "@material-ui/icons";
import axios from "axios";
import { useEffect, useState } from "react";
import Modal from "react-modal";
import { useDispatch, useSelector } from "react-redux";
import { useActionData } from "react-router-dom";
import { commentActions } from "../../store/CommentSlice";



const Comment = (props) => {
const commentdetails = useSelector((state)=>state.commentdata)
const [comment,setComment] = useState();
useEffect(()=>{
  async function handleComment(e) {
    try {
      await dispatch(commentActions.setCommentId(randomNumberInRange(1,100)))
      await dispatch(commentActions.setCommentPostId(99))
      await dispatch(commentActions.setCommentUsername("iloveulaav"));
      const  commenturl = `http://localhost:8000/api/comments/`;
      const commentdata = await axios.post(commenturl,commentdetails).then(response => {
        setComment(response.data);
      })
      console.log(commentdetails);
    }
    catch (e) {
      console.log(e);
    }
  }
})
const dispatch = useDispatch();

  return (
    <div key = {props.keyval}>
        <input value={commentdetails.comment} onChange={(e)=>{
          dispatch(commentActions.setUserComment(e.target.value));
        }}>Comment</input>
      
    </div>
  )
}
export default Comment;
