const LoadComments = ()=>{

    const [listComment, setListComment] = useState([]);

    useEffect(() => {
  
      async function getComments() {
        try {
          const posturl = `http://localhost:8000/api/cmts/${props.postid}`;
          const postdata = await axios.get(posturl).then(response => {
            setListComment(response.data);
          })
        }
        catch (e) {
          console.log(e);
        }
      }
      try {
        getComments();
      }
      catch (e) {
        console.log(e);
      }
    },[])
  

    return (
        <div>
          <div className="post__answer">
          {
            listComment.map((list) => {
              return (
                <div>
                  <p key={props.id} style={{ position: "relative", paddingBottom: "5px" }}>
                    {props.id === list.postid ? (
                      <span>
                        {list.comment}
                        <br />
                        /    <span
                          style={{
                            position: "absolute",
                            color: "gray",
                            fontSize: "small",
                            display: "flex",
                            right: "0px",
                          }}
                        >
                          <span style={{ color: "#b92b27" }}>
                            {list.username
                              ? list.username
                              : "unidentified"}

                          </span>
                        </span>
                      </span>
                    ) : (
                      ""
                    )}
                  </p>
                </div>
              )
            })}
        </div>  
        </div>
    )
}
export default LoadComments;
