    try{
      setComment(e.target.value)
      dispatch(commentActions.setUserComment(e.target.value));
    }
    catch(e){
      console.log(e);
    }
}

  async function handleSubmit() {
    try {
      dispatch(commentActions.setCommentId(randomNumberInRange(1,100)));
      dispatch(commentActions.setCommentUsername("iloveulaav"));
