                  {new Date().getDate()} 
                </span>
              </p>
            </div>
            <div className="modal__answer">
              <textarea
                value={comment}
                onChange={(e,val1) =>{ 
                  dispatch(commentActions.setCommentPostId("gggggg"));
                  dispatch(commentActions.setUserComment("gggg"));
                  dispatch(commentActions.setCommentPostId(2));
                  setComment(e.target.value)
                }}
                placeholder="Enter Your Answer"
                type="text"
              />
              {console.log(commentdetails)}
            </div>
