import axios from "axios";
import { useEffect, useState } from "react";
import "./LoadComment.css"

const LoadComments = (props) => {

    const [listComment, setListComment] = useState([]);


    useEffect(() => {

        async function getComments() {
            try {
                const posturl = `http://localhost:8000/api/cmts/${13}`;
                const postdata = await axios.get(posturl).then(response => {
                    setListComment(response.data);
                })
            }
            catch (e) {
                console.log(e);
            }
        }
        try {
            getComments();
        }
        catch (e) {
            console.log(e);
        }
    }, [])

    return (
        <div>
            {
                listComment.map((list) => {
                    return (
                        <div key={list.id} className="comments_list">
                            <div>
                            <p key={props.postid} ></p>
                            </div>
                            <div className="contents">
                                <h3 style={{ color: "#b92b27" }}>
                                            {list.username}
                                </h3>
                                <h3 style={{marginTop:"20px",marginLeft:"5px",fontFamily:"-moz-initial"}}>{list.comment}</h3>
                                
                            </div>


                        </div>
                    )
                })}
        </div>
    )
}
export default LoadComments;
